CREATE TABLE IF NOT EXISTS `list` (
  `ID` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '新闻ID',
  `title` varchar(20) NOT NULL COMMENT '新闻标题',
  `content` varchar(500) NOT NULL COMMENT '新闻内容',
  `c_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  `author` varchar(20) NOT NULL COMMENT '作者',
  `start` int(2) NOT NULL DEFAULT '1' COMMENT '状态监控',
  PRIMARY KEY (`ID`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=14 ;

