CREATE TABLE IF NOT EXISTS `news` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '新闻id',
  `username` char(20) NOT NULL COMMENT '用户名',
  `title` char(50) NOT NULL COMMENT '标题',
  `content` varchar(2500) NOT NULL COMMENT '内容',
  `image` char(50) NOT NULL COMMENT '头像',
  `banimg` char(50) NOT NULL COMMENT '版面图片',
  `c_time` char(20) NOT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=21 ;

