--
-- 表的结构 `skills`
--

CREATE TABLE IF NOT EXISTS `skills` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `skillname` char(20) NOT NULL,
  `skillinfo` varchar(500) NOT NULL,
  `image` char(50) NOT NULL,
  `pid` int(10) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=13 ;

--
-- 转存表中的数据 `skills`
--

INSERT INTO `skills` (`id`, `skillname`, `skillinfo`, `image`, `pid`) VALUES
(1, '山鬼·白鹿', '被动：瑶被鹿灵守护着，当被晕眩，击退，击飞等硬控技能击中会立刻解除控制并变成鹿灵6秒（除压制效果以外）每30秒最多触发一次；鹿灵状态将会获得无法被攻击但也失去自己攻击别人的能力，灵鹿静怡的灵光会持续照拂敌人，减速30%。', '../images/index/hero/50500.png', 1),
(2, '若有人兮', '举杖引魄击退身边的敌人，能量波向前飞行造成法术伤害，在快飞到终点前会检测范围内的英雄并化成第二段能量球跟踪打击造成法术伤害和0.75秒晕眩和显形效果。', '../images/index/hero/50510.png', 1),
(3, '风飒木萧', '瑶持续5秒自动打击身边随机的敌人，最多3个目标，每次攻击都会造成法术伤害，技能开启期间内无法普攻。', '../images/index/hero/50520.png', 1),
(4, '独立兮山之上', '瑶附身到其他英雄，赋予附灵目标真实护盾（抵挡真实伤害），附身状态技能获得增强并且刷新所有技能冷却。附身状态：盾破会被击落下来进入CD，主动脱离附灵目标会返还50%冷却时间', '../images/index/hero/50530.png', 1),
(5, '盘古斧', '被动：盘古的斧头具有10点耐久度，每次普攻命中敌方英雄会消耗1点耐久度，消耗完后斧头会破碎变成两只能量臂出现在盘古的身体两侧，切换瞬间盘古无法攻击并不可被选中，每20秒斧头会自动回复1点耐久度，切换形态时会清空1，2技能50%的最大冷却时间。盘古斧：盘古使用斧头时，每次普攻命中敌人会使敌人的伤口裂开，在之后的2秒时间内持续流血每0.5秒造成对方2%最大生命值的物理伤害，流血伤害不触发末世。能量臂：盘古使用能量臂时，每次普攻和技能命中都会在敌人身上叠加1层眩晕标记，满5层会使敌人眩晕1秒并触发额外200（+100%物理加成）点物理伤害，此效果对同一目标有3秒的冷却时间。', '../images/index/hero/pangu1.png', 2),
(6, '狂怒突袭', '盘古朝指定位置跳跃并获得持续3秒50%的移速增加效果，落地时对内圈的敌人造成200/240/280/320/360/400（+100%物理加成）点物理伤害，如果落地时攻击到敌人加速效果会被移除。盘古斧：在跳跃期间盘古可以再次使用狂怒突袭选中一名敌人，落地后盘古会立刻冲向选中的敌人并将其击退造成100/130/160/190/220/250（+60%物理加成）点物理伤害并自己停下，同时在3秒内增加10/20/30/40/50/60%的攻击速度。能量臂：处于能量臂状态下时，将无法使用第二次狂怒突袭冲击敌人', '../images/index/hero/pangu3.png', 2),
(7, '压制之握', '盘古在目标身边召唤正义之手对敌人进行攻击，如果被正义之手握住，敌人会受到200/230/260/290/320/250（+75%物理加成）点物理伤害，并减少50%移动速度，持续2秒，期间盘古的普攻对其造成伤害时会回复自身6%最大百分比的血量。盘古斧：当盘古拥有斧头时，压制之握还会使敌人被缴械，缴械期间敌人无法使用普通攻击，持续2秒。能量臂：处于能量臂状态下时，压制之握将不具备缴械效果', '../images/index/hero/pangu4.png', 2),
(8, '开天辟地', '盘古向上跃起，将力量汇聚到斧头上。力量汇聚完毕后盘古会将斧头朝前劈下，地面会朝前逐渐裂开，被地面的裂纹触碰的敌人会受到600/900/1200（+120%物理加成）点物理伤害并减少90%移动速度，持续2秒；减速效果在2秒内持续衰减，期间敌人被缴械；蓄力期间盘古可以吸收附近友方英雄的力量，每吸收1个就能多劈出一道裂缝，最多劈出5道；当多道裂缝命中同一个目标时，从第二道裂缝开始将只造成25%的伤害。此技能只有在盘古拥有斧头时才可以使用，蓄力期间盘古处于霸体状态。蓄力期间被打断会返还部分冷却时间。', '../images/index/hero/pangu2.png', 2),
(9, '毫发无伤', '被动：猪八戒损失的生命会以残血形态暂存，暂存期间猪八戒对英雄\\非英雄造成伤害50%\\25%(计算忽略敌方免伤)的等值残血会转化为真实血量。残血产生7秒后开始以每秒3%最大生命值的速度自动消减。九齿钉耙乃神兵，普攻附带40（+0.5%最大生命加成）点物理伤害', '../images/index/hero/51100.png', 3),
(10, '肉弹蹦床', '猪八戒奋力跳起，落地时对砸到的敌人造成150/165/180/195/210/225（+8%最大生命加成）点物理伤害。之后弹性极佳的身体会再次高高弹起，并在落地时造成相同伤害，以及0.5秒晕眩效果。当同时攻击多个敌方英雄时，每增加一位敌方英雄衰减15%的伤害，最低衰减至初始的70%。再次落地时由于惯性极大，猪八戒会快速翻滚一段距离。跳跃期间猪八戒处于霸体状态', '../images/index/hero/51110.png', 3),
(11, '倒打一耙', '猪八戒将钉耙变长直击前方敌人，造成150/180/210/240/270/300（+50%物理加成）点物理伤害和1.5秒60%减速效果。钉耙若嵌入地形，猪八戒会借力冲撞至嵌入处，并将路径上敌人击退至该处。猪八戒撞击地形时，造成150/165/180/195/210/225/（+6%最大生命加成）点物理伤害和0.5秒晕眩。被动：使用倒打一耙后，下次普攻会用钉耙把敌人拉回来', '../images/index/hero/51120.png', 3),
(12, '圈养时刻', '猪八戒筑起一堵高墙，英雄无法移动穿越；墙最多存在4秒；筑起瞬间对身前敌人造成250/325/400/(（+90%物理加成）点物理伤害；施法时猪八戒霸体', '../images/index/hero/51130.png', 3);

