--
-- 表的结构 `skins`
--

CREATE TABLE IF NOT EXISTS `skins` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '皮肤id',
  `heroname` char(10) NOT NULL COMMENT '英雄名称',
  `skinname` char(10) NOT NULL COMMENT '皮肤名称',
  `image` char(50) NOT NULL COMMENT '皮肤图片',
  `gifimage` char(50) NOT NULL COMMENT '英雄gif图',
  `protype` char(50) NOT NULL COMMENT '英雄原型图片',
  `content` varchar(1000) NOT NULL COMMENT '皮肤介绍',
  `video` char(50) NOT NULL COMMENT '皮肤视频',
  `updatetime` char(20) NOT NULL COMMENT '发布时间',
  `p_id` int(10) NOT NULL COMMENT '对应英雄id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=11 ;

--
-- 转存表中的数据 `skins`
--

INSERT INTO `skins` (`id`, `heroname`, `skinname`, `image`, `gifimage`, `protype`, `content`, `video`, `updatetime`, `p_id`) VALUES
(1, '典韦', '蓝屏警告', '../images/index/skin/dianwei.jpg', '../images/index/skin/dianweigif.gif', '../images/index/skin/dianweiman.jpg', '熟悉典韦的玩家都知道，典韦是一个“疯狂”的英雄，怎么样才能让一个皮肤符合这个气质呢？ 皮肤讨论会的时候，我们试着问自己：什么事情能让你奔溃到发狂？——“加班写的代码，突然因为蓝屏而全部丢失，真的是一个令人奔溃到发狂的情况”。有同事提出这个方案，我们觉得真的很符合。不知道各位玩家是不是也遇到过这样的场景：可能是熬夜写的策划案，也可能是正在准备的答辩论文或者某个设计稿……. 典韦的新皮肤【蓝屏警告】正是想还原这样一个能得到大家共鸣的片段，并且我们选择了程序员这一有代表性的身份。独立的格子间位置、相对比较潮流青春休闲装扮和背后的标语，炫彩的机械键盘、工牌等等，期望在还原身份的同时有具备一定的艺术戏剧效果。游戏内的技能特效也会围绕键盘为武器来制作，包括每个技能对应的人物台词也是匹配了程序员的身份。 希望大家能喜欢这次的设计，我们也会密切关注大家的反馈。希望大家喜欢这种有趣的、具有共鸣感的设计，我们也曾讨论是否是可以通过皮肤给大家展示诸如游戏策划、音频策划、皮肤设计师等是如何工作的。', '1354', '2019-04-16', 11),
(2, '女娲', '朔望之晖', '../images/index/skin/nvwa.jpg', '../images/index/skin/nvwagif.gif', '../images/index/skin/nvwaman.png', '我们这次希望给玩家一个不一样的女娲皮肤感受，我们想要设计一个苗族崇拜的女神，最终选定了女娲原型：女娲传说在各个民族流传深远，至今苗族等还将女娲作为民族始祖加以崇拜。这个皮肤在形象上也选取了苗族服装头饰上最有特点的银片和银项，深蓝色的上衣有苗族特有的花纹蜡染；将女娲背后的光环设计为月亮光辉，也想表达苗族跳月之意——沐浴在月亮的光辉下跳起苗族舞蹈，这一风俗叫做跳月，大厅动画中女娲刚好在飞舞着创造着”方舟核心“。在宣传图的设计上女神躺坐。树心飞出蝴蝶，这个画面则是来自于苗族古老的歌谣。讲述了苗族的起源。枫树是对民族的崇拜。蝴蝶图腾是对生命形式的敬意。', '35252', '2019-04-16', 12),
(3, '牛魔', 'S15赛季御胜', '../images/index/skin/niumo.jpg', '../images/index/skin/niumogif.gif', '../images/index/skin/niumoman.jpg', '牛魔是排位赛皮肤系列的第三个英雄，在皮肤故事观中他在后面会叛变队友（狂铁、米莱狄），于是就确定了两个基本的设计点 ：延续排位赛皮肤的图形元素与构成且要能体现出牛魔的叛变。 整个系列皮肤都以龙元素作为整体包装，于是我们将牛魔巨大的肩甲设计成了两条卧龙，武器设计为龙头；同时我将手臂的包甲、膝盖、脚趾也用了龙爪这种更为尖锐的形状语言，与以往牛魔皮肤偏向圆润的外形拉开差距，也延续了赛季皮肤配色。 牛魔叛变在视觉上的呈现是要看起来和狂铁米莱狄是一个阵营但同时又要有所区别，于是我们想到在颜色上做区分：米莱狄狂铁他们能量体的发光都是黄色，是更正向的色彩；而牛魔采用的是更偏邪恶感受的蓝紫色，以此来暗示牛魔后面的叛变。', '352523', '2019-03-15', 13),
(4, '达摩', '黄金狮子座', '../images/index/skin/damo.jpg', '../images/index/skin/damogif.gif', '../images/index/skin/damoman.jpg', '在此次选择与《圣斗士星矢（腾讯）》手游合作的形象时，我们一致认为达摩与《圣斗士星矢》中的狮子座艾欧里亚能摩擦出特别的火花：《圣斗士星矢》中，狮子座艾欧里亚是一名物理攻击突出、综合实力强劲的圣斗士。皮肤造型设计时，我们在还原圣斗士经典狮子座形象的基础上，丰富了皮肤的细节，使其更加帅气且有设计感；技能特效上也还原了狮子座等离子光速拳的切光效果。一个集浓浓情怀与超高颜值于一体的“黄金狮子座皮肤”，诞生于此。', '2355', '2019-03-08', 14),
(5, '孙悟空', '大圣娶妻', '../images/index/skin/sunwukong.jpg', '../images/index/skin/sunwukonggif.gif', '../images/index/skin/sunwukongman.jpg', '当知道要设计这两款皮肤的时候，其实我们是非常激动的。 大话西游是一部我们童年经典的电影，里面很多的场景、桥段、音乐我们都记忆犹新。十年了，我们永远忘不掉电影里最后一幕的那句台词“那人好像一条狗啊”。永远忘不掉那句经典的话语“曾经有一份真挚的爱情摆在我的面前，但是我没有珍惜……” 我们每个人心中，可能都有个紫霞，在我们情窦初开的时候，在我们的心里留下一滴泪，因为我们的不勇敢、因为我们的不作为而失去了。这次，我们想给这对苦命鸳鸯一个完美的结局。 希望在王者峡谷里，紫霞和至尊宝是幸福的，是能够甜甜蜜蜜在一起的。至尊宝是不依赖金箍的力量，而是靠自己的力量去拯救紫霞的。希望能圆我们每个玩家，心中的那一份遗憾。 因此，展示动画部分，在至尊宝的一侧，我们演绎了至尊宝回忆起经典的那个桥段，紫霞离开，至尊宝从伤心欲绝到重新振作，召唤金箍棒目光坚定，准备踏上抢亲之路。 在紫霞的一侧，我们还原了电影里的场景，重新设计了至尊宝抢亲成功的完美结局。在至尊宝入场、紫霞的反应上逐帧还原电影里二人的每个动作和神态。 短短十秒的展示动画，也许这个结局，离我们每位观众，每个玩家心中那最完美的结局还有一些距离，但我们希望，通过这个皮肤，能够让大家回想起曾经心里的那个紫霞，那个至尊宝，回想起那份悸动，同时更好的去珍惜自己身边的，那个值得你付出一生的她。', '521346', '2019-03-06', 34),
(6, '苏烈', '玄武志', '../images/index/skin/sulie.jpg', '../images/index/skin/suliegif.gif', '../images/index/skin/sulieman.jpg', '“据《白泽图》记载，上古之时，群妖现世，有五神兽化为凡人之姿，分守五方天地……”\n这是不同的战场，但守护长城和人间天地的初心未改。\n\n“天反时为灾，地反物为妖。”\n在这个系列的背景故事中，传言妖怪祸乱人间，人人皆以为不详。于是众生祈盼虚无的救赎，比如瑞兽，比如吉兆的天象。\n便有了五神兽降世，化为凡人之姿，分守五方天地的传说……\n\n长城，耸立成中原最坚实的屏障。\n今天，又是和平的一天。\n在这个传说中，天降神兽，解决祸乱的妖灾，拯救苍生。\n但于五位神兽来说，其实是一段真实的，有血有生死的历史。\n苏烈化身玄武，善守，守北方，自玄武降世，大陆之北，竟无一妖邪精怪能突破防线半分。\n\n也许此刻，他们还在各自巡守的烽火台，遥看边城新年的灯火。\n也只有他们明白，那都是拼出生命才换来的团圆夜，力战到底也要守护的家国人间。\n\n\n背景故事\n\n他依然记得分别的那天，美丽而强大的战士将重剑插入足下的土地，抬起手臂，指向遥远的西方：\n“那里，是长城，是保卫这片净土不被妖兽侵入的重要关卡。”\n他顺着女性战士手臂所指的方向望去，茫茫沙尘后，难掩去的是层峦叠嶂，巍峨群山。\n在这里，他看不见她口中所说的“长城”，却能想象出高耸的城墙是如何延绵不绝，土石筑成的壁垒以何等坚不可摧的姿态守护着万物苍生。', '142242', '2019-03-06', 25),
(7, '凯', '青龙志', '../images/index/skin/kai.jpg', '../images/index/skin/kaigif.gif', '../images/index/skin/kaiman.jpg', '“据《白泽图》记载，上古之时，群妖现世，有五神兽化为凡人之姿，分守五方天地……” 这是不同的战场，但守护长城和人间天地的初心未改。  “天反时为灾，地反物为妖。” 在这个系列的背景故事中，传言妖怪祸乱人间，人人皆以为不详。于是众生祈盼虚无的救赎，比如瑞兽，比如吉兆的天象。 便有了五神兽降世，化为凡人之姿，分守五方天地的传说……  长城，耸立成中原最坚实的屏障。 今天，又是和平的一天。 在这个传说中，天降神兽，解决祸乱的妖灾，拯救苍生。 但于五位神兽来说，其实是一段真实的，有血有生死的历史。 铠化身青龙，他攻守兼备，守东方，以雷霆之势进攻妖兽巢穴，所到之处，妖邪伏诛，鬼怪披靡。  也许此刻，他们还在各自巡守的烽火台，遥看边城新年的灯火。 也只有他们明白，那都是拼出生命才换来的团圆夜，力战到底也要守护的家国人间。', '457545', '2019-02-06', 28),
(8, '百里玄策', '白虎志', '../images/index/skin/bailixuance.jpg', '../images/index/skin/bailixuancegif.gif', '../images/index/skin/bailixuanceman.jpg', '“据《白泽图》记载，上古之时，群妖现世，有五神兽化为凡人之姿，分守五方天地……” 这是不同的战场，但守护长城和人间天地的初心未改。  “天反时为灾，地反物为妖。” 在这个系列的背景故事中，传言妖怪祸乱人间，人人皆以为不详。于是众生祈盼虚无的救赎，比如瑞兽，比如吉兆的天象。 便有了五神兽降世，化为凡人之姿，分守五方天地的传说……  长城，耸立成中原最坚实的屏障。 今天，又是和平的一天。 在这个传说中，天降神兽，解决祸乱的妖灾，拯救苍生。 但于五位神兽来说，其实是一段真实的，有血有生死的历史。 玄策化身白虎，善攻，守西方，历经大小百余战，以致大陆之西，妖祟绝迹。  也许此刻，他们还在各自巡守的烽火台，遥看边城新年的灯火。 也只有他们明白，那都是拼出生命才换来的团圆夜，力战到底也要守护的家国人间。', '54754', '2019-02-06', 26),
(9, '百里守约', '朱雀志', '../images/index/skin/bailishouyue.jpg', '../images/index/skin/bailishouyuegif.gif', '../images/index/skin/bailishouyueman.jpg', '“据《白泽图》记载，上古之时，群妖现世，有五神兽化为凡人之姿，分守五方天地……” 这是不同的战场，但守护长城和人间天地的初心未改。  “天反时为灾，地反物为妖。” 在这个系列的背景故事中，传言妖怪祸乱人间，人人皆以为不详。于是众生祈盼虚无的救赎，比如瑞兽，比如吉兆的天象。 便有了五神兽降世，化为凡人之姿，分守五方天地的传说……  长城，耸立成中原最坚实的屏障。 今天，又是和平的一天。 在这个传说中，天降神兽，解决祸乱的妖灾，拯救苍生。 但于五位神兽来说，其实是一段真实的，有血有生死的历史。 守约化身朱雀，神鬼莫测，守南方，一日之内，连杀妖怪首领十余人，顷刻间群妖授首，无不胆寒。  也许此刻，他们还在各自巡守的烽火台，遥看边城新年的灯火。 也只有他们明白，那都是拼出生命才换来的团圆夜，力战到底也要守护的家国人间。', '146545', '2019-02-06', 27),
(10, '明世隐', '虹云星宫', '../images/index/skin/mingshiyin.jpg', '../images/index/skin/mingshiyinggif.gif', '../images/index/skin/mingshiyingman.jpg', '在本次的设计中，我们希望明世隐是一个逍遥洒脱的仙君，在云上过着对酒当歌的日子，但是内心中他是一个很孤独的人，他会兴致勃勃的将自己的法器当做玩具供宴会的众人把玩，也会一个人安静的靠着阁楼看月亮。偶尔闲下来的空虚是他最放松的时候。每天的生活热闹又清凄，觥筹交错中有一些自己的小秘密，但还算享受自己的自洽。这是一个很轻松的人设，希望大家喜欢。', '27557', '2019-01-30', 23);

