--
-- 表的结构 `videos`
--

CREATE TABLE IF NOT EXISTS `videos` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '视频id',
  `username` char(20) NOT NULL COMMENT '用户名',
  `title` char(50) NOT NULL COMMENT '视频标题',
  `video` char(50) NOT NULL COMMENT '视频地址',
  `poster` char(50) NOT NULL COMMENT '视频图片',
  `image` char(50) NOT NULL COMMENT '用户头像',
  `tab` char(50) NOT NULL COMMENT '简介',
  `type_id` int(10) NOT NULL COMMENT '视频类型id',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8mb4 AUTO_INCREMENT=21 ;

